defmodule Solution1143Test do
	use ExUnit.Case
	alias Solution1143

  test "case 1" do
    assert 3 == Solution1143.longest_common_subsequence("abcde", "ace")
  end

  test "case 2" do
    assert 3 == Solution1143.longest_common_subsequence("abc", "abc")
  end

  test "case empty" do
    assert 0 == Solution1143.longest_common_subsequence("abc", "")
  end

  test "case one letter" do
    assert 1 == Solution1143.longest_common_subsequence("abc", "c")
    assert 1 == Solution1143.longest_common_subsequence("c", "abc")
  end

  test "case 12" do
    assert 1 == Solution1143.longest_common_subsequence("psnw", "vozsh")
  end

  test "case looooong strings" do
    str1 = ""
    str2 = ""
    assert 323 == Solution1143.longest_common_subsequence(str1, str2)
  end

end
